package com.shan.controller;

import com.deepoove.poi.XWPFTemplate;
import com.deepoove.poi.data.HyperLinkTextRenderData;
import com.deepoove.poi.data.PictureRenderData;
import com.deepoove.poi.data.RenderData;
import com.deepoove.poi.data.TextRenderData;
import com.deepoove.poi.util.BytePictureUtils;
import com.shan.domain.SysDept;
import io.swagger.annotations.Api;
import lombok.extern.slf4j.Slf4j;
import org.apache.commons.codec.binary.Base64;
import org.apache.commons.compress.archivers.zip.Zip64Mode;
import org.apache.commons.compress.archivers.zip.ZipArchiveEntry;
import org.apache.commons.compress.archivers.zip.ZipArchiveOutputStream;
import org.springframework.core.io.ClassPathResource;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;

import javax.imageio.ImageIO;
import javax.servlet.http.HttpServletResponse;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.net.URLEncoder;
import java.text.SimpleDateFormat;
import java.util.*;

/**
 * @author sc
 * @createTime 2020/1/14 17:51
 * @description
 */
@Api(description = "poi操作word")
@Slf4j
@Controller
public class CmsController {


    /**
     * poi导出word  以及压缩包
     * @return
     */
    @GetMapping("/testWord")
    public void testWord(HttpServletResponse response) throws IOException {

        byte[] base64 = Base64.decodeBase64("");
        ByteArrayInputStream in = new ByteArrayInputStream(base64);
        BufferedImage image = ImageIO.read(in);
        Map<String, Object> datas = new HashMap<String, Object>() {
            {
                put("Name", new TextRenderData("33ff33", "Alex Bin"));
                put("Age", "24");
                put("title", "发发发");
                put("date",  new SimpleDateFormat("yyyy-MM-dd").format(new Date()));
                //超链接
                //put("Sources", new HyperLinkTextRenderData("baidu", "http://www.baidu.com"));
                // 本地图片 可以绝对路径但不推荐
                put("Image", new PictureRenderData(120, 120, new ClassPathResource("static/1.png").getFile().getPath()));
                //java图片/base64等格式图片
          put("JavaImage", new PictureRenderData(600, 350, ".png", BytePictureUtils.getBufferByteArray(image)));
            }
        };
        ClassPathResource oldDoc = new ClassPathResource("templates/TL.docx");
        String filePath = oldDoc.getFile().getPath();
        XWPFTemplate template = XWPFTemplate.compile(filePath)
                .render(datas);
        ByteArrayOutputStream out = new ByteArrayOutputStream();
        template.write(out);
        String zipFileName = "压缩包.zip";
        response.setHeader("Content-Disposition","attachment; fileName=" + URLEncoder.encode(zipFileName, "UTF-8"));
        ZipArchiveOutputStream zipOut = new ZipArchiveOutputStream(response.getOutputStream());
        zipOut.setUseZip64(Zip64Mode.AsNeeded);
        zipOut.setEncoding("UTF-8");
        String fileName ="poi导出word测试.docx";
        ZipArchiveEntry zipEntry = new ZipArchiveEntry(fileName);
        zipEntry.setSize(out.size());
        zipOut.putArchiveEntry(zipEntry);
        zipOut.write(out.toByteArray());
        zipOut.closeArchiveEntry();
        zipOut.flush();
        zipOut.finish();
    }


    /**
     * poi导出word
     * @return
     */
    @GetMapping("/testWord2")
    public void testWord2(HttpServletResponse response) throws IOException {

        byte[] base64 = Base64.decodeBase64("");
        ByteArrayInputStream in = new ByteArrayInputStream(base64);
        BufferedImage image = ImageIO.read(in);
        Map<String, Object> datas = new HashMap<String, Object>() {
            {
                put("Name", new TextRenderData("33ff33", "Alex Bin"));
                put("Age", "24");
                put("title", "发发发");
                put("date", new SimpleDateFormat("yyyy-MM-dd").format(new Date()));
                //超链接
                put("Sources", new HyperLinkTextRenderData("baidu", "http://www.baidu.com"));
                // 本地图片 可以绝对路径但不推荐
                put("Image", new PictureRenderData(120, 120, new ClassPathResource("static/1.png").getFile().getPath()));
                //java图片/base64等格式图片
                put("JavaImage", new PictureRenderData(600, 350, ".png", BytePictureUtils.getBufferByteArray(image)));
            }
        };
        response.setHeader("Content-Disposition", "attachment; fileName=" + URLEncoder.encode("poi导出word测试.docx", "UTF-8"));
        ClassPathResource oldDoc = new ClassPathResource("templates/TL.docx");
        String filePath = oldDoc.getFile().getPath();
        XWPFTemplate template = XWPFTemplate.compile(filePath)
                .render(datas);
        template.write(response.getOutputStream());

        template.close();
    }



    /**
     * poi导出word list数据
     * @return
     */
    @GetMapping("/testWord3")
    public void testWord3(HttpServletResponse response) throws IOException {

        List<Map<String, RenderData>> dclrUserList = new LinkedList<>();
        Map<String, RenderData> dclrUserRow1 = new LinkedHashMap<>();
        dclrUserRow1.put("Name", new TextRenderData("aaaa"));
        Map<String, RenderData> dclrUserRow2= new LinkedHashMap<>();
        dclrUserRow2.put("Name", new TextRenderData("bbbb"));
        dclrUserList.add(dclrUserRow1);
        dclrUserList.add(dclrUserRow2);

        Map<String, Object> data1 = new HashMap<String, Object>() ;
        data1.put("title", "发发发");
        data1.put("date", new SimpleDateFormat("yyyy-MM-dd").format(new Date()));
        data1.put("list", dclrUserList);
        response.setHeader("Content-Disposition", "attachment; fileName=" + URLEncoder.encode("poi导出word测试list.docx", "UTF-8"));
        ClassPathResource oldDoc = new ClassPathResource("templates/listDemo.docx");
        String filePath = oldDoc.getFile().getPath();
        XWPFTemplate template = XWPFTemplate.compile(filePath)
                .render(data1);
        template.write(response.getOutputStream());
        template.close();
    }


}
